import React from 'react';
import { LineChart, AreaChart, BarChart } from '../component/Charts';
import DataLog from './DataLogs';
import { useAuth } from '../context/AuthProvider';
import { useNavigate } from 'react-router-dom';

const Dashboard: React.FC = () => {
    const { currentUser } = useAuth();
    const navigate = useNavigate();

    // Redirect to login if user is not authenticated
    if (!currentUser) {
        navigate('/login');
        return null; // Return null to prevent rendering further content
    }

    return (
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-4 h-screen p-4 lg:p-8">
            {/* Left Section */}
            <div className="lg:col-span-2 xl:col-span-1 grid grid-cols-1 gap-4">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <div className="sm:col-span-1">
                        <LineChart />
                    </div>
                    <div className="sm:col-span-1">
                        <AreaChart />
                    </div>
                    <div className="sm:col-span-1">
                        <LineChart />
                    </div>
                    <div className="sm:col-span-1">
                        <AreaChart />
                    </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mt-4">
                    <div className="sm:col-span-2">
                        <BarChart />
                    </div>
                    <div className="sm:col-span-2">
                        <BarChart />
                    </div>
                </div>
            </div>

            {/* Right Section - DataLog */}
            {/* <div className="hidden lg:block lg:col-span-1 xl:col-span-2">
                <DataLog />
            </div> */}
        </div>
    );
};

export default Dashboard;
