import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthProvider';

const Login: React.FC = () => {
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [error, setError] = useState<string>('');
    const navigate = useNavigate();
    const { signin } = useAuth();

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setError('');
        try {
            await signin(email, password);
            navigate('/account');
        } catch (e: any) {
            setError(e.message);
            console.log(e.message);
        }
    };

    return (
        <div className='w-full h-screen'>
            {/* set image to cover the whole background with gradient */}
            <img
                className='hidden sm:block absolute w-full h-full object-cover'
                src='https://i.pinimg.com/originals/13/8a/64/138a641c5961581d2cb236c6d18e74f6.jpg'
                alt='/'
            />
            <div className='dark:bg-black/60 fixed top-0 left-0 w-full h-screen'></div>
            <div className='fixed w-full px-4 py-24 z-50'>
                <div className='max-w-[450px] h-[600px] mx-auto bg-black/75 text-white'>
                    <div className='max-w-[320px] mx-auto py-16'>
                        <div className="mt-5">
                            <h3 className="text-gray-800 text-2xl font-bold sm:text-3xl">Log in to your account</h3>
                        </div>
                        {error && <p className='p-3 bg-red-400 my-2'>{error}</p>} 
                        <form onSubmit={handleSubmit} className='w-full flex flex-col py-4'>
                            <div>
                                <label className="font-medium">
                                    Email
                                </label>
                                <input
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg"                                    type='email'
                                    placeholder='Email'
                                    autoComplete='email'
                                    required
                                />
                            </div>

                            <div>
                                <label className="font-medium">
                                    Password
                                </label>


                            </div>

                            <input
                                onChange={(e) => setPassword(e.target.value)}
                                className='w-full px-3 py-2 border rounded-md dark:border-gray-300 dark:bg-gray-50 dark:text-gray-800'
                                type='password'
                                placeholder='Password'
                                autoComplete='current-password'
                                required
                            />
                            <button type='submit' className='bg-green-600 py-3 my-6 rounded font-bold'>
                                Sign In
                            </button>
                            <div className='flex justify-between items-center text-sm text-gray-600'>
                                <p>
                                    <input className='mr-2' type='checkbox' />
                                    Remember me
                                </p>
                                <p>Need Help?</p>
                            </div>
                            <p className='py-8'>
                                <span className='text-gray-600'>Create Account?</span>{' '}
                                <Link to='/signup'>Sign Up</Link>
                            </p>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
