// src/components/LineChart.tsx

import React from 'react';
import ReactApexChart from 'react-apexcharts';

export const LineChart: React.FC = () => {
    const options = {
        chart: {
            type: 'line'
        },
        series: [
            {
                name: 'Sensor Data',
                data: [10, 41, 35, 51, 49, 62, 69, 91, 148]
            }
        ],
        xaxis: {
            categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep']
        }
    };

    return (
        <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Line Chart</h2>
            <ReactApexChart options={options} series={options.series} type="line" height={300} />
        </div>
    );
};



export const AreaChart: React.FC = () => {
    const options = {
        chart: {
            type: 'area'
        },
        series: [
            {
                name: 'Sensor Data',
                data: [10, 41, 35, 51, 49, 62, 69, 91, 148]
            }
        ],
        xaxis: {
            categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep']
        }
    };

    return (
        <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Area Chart</h2>
            <ReactApexChart options={options} series={options.series} type="area" height={300} />
        </div>
    );
};

export default AreaChart;


export const BarChart: React.FC = () => {
    const options: ApexOp = {
        chart: {
            type: 'bar'
        },
        series: [
            {
                name: 'Sensor Data',
                data: [10, 41, 35, 51, 49, 62, 69, 91, 148]
            }
        ],
        xaxis: {
            categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep']
        }
    };

    return (
        <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Bar Chart</h2>
            <ReactApexChart options={options} series={options.series} type="bar" height={200} />
        </div>
    );
};
