import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthProvider';

const Login: React.FC = () => {
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [error, setError] = useState<string>('');
    const navigate = useNavigate();
    const { signin } = useAuth();

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setError('');
        try {
            // await signin(email, password);
            const fakeUser = { email: 'bms-1248@gmail.com', password: 'password' }; // Replace with your fake user data
            if (email === fakeUser.email && password === fakeUser.password) {
                // Simulate sign-in success
                localStorage.setItem('currentUser', JSON.stringify({ email: fakeUser.email }));
                await signin(email, password); // Call your actual signin function if needed
                navigate('/account');
            } else {
                throw new Error('Invalid credentials');
            }
            navigate('/account');
        } catch (e: any) {
            setError(e.message);
            console.log(e.message);
        }
    };

    return (
        <div className='w-full h-screen'>
            {/* set image to cover the whole background with gradient */}
            <img
                className='hidden sm:block absolute w-full h-full object-cover'
                src='https://i.pinimg.com/originals/13/8a/64/138a641c5961581d2cb236c6d18e74f6.jpg'
                alt='/'
            />
            <div className='dark:bg-black/60 fixed top-0 left-0 w-full h-screen'></div>
            <div className='fixed w-full px-4 py-24 z-50'>
                <div className='max-w-[450px] h-[600px] mx-auto bg-black/75 text-white'>
                    <div className='max-w-[320px] mx-auto py-16'>
                        <div className="mt-5">
                            <h3 className="text-gray-200 text-2xl font-bold sm:text-3xl">Log in to your account</h3>
                        </div>
                        {error && <p className='p-3 bg-red-400 my-2'>{error}</p>} 
                        <form onSubmit={handleSubmit} className='w-full flex flex-col py-4'>
                            <div>
                                <label className="font-medium">
                                    Email
                                </label>
                                <input
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg"                                    type='email'
                                    placeholder='Email'
                                    autoComplete='email'
                                    required
                                />
                            </div>

                            <div>
                                <label className="font-medium">
                                    Password
                                </label>

                                <input
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg"
                                    type='password'
                                    placeholder='Password'
                                    autoComplete='current-password'
                                    required
                                />

                            </div>


                            <button type='submit' className='bg-green-600 py-3 my-6 rounded font-bold'>
                                Sign In
                            </button>
                            <div className='flex justify-between items-center text-sm text-gray-600'>
                                <p>
                                    <input className='mr-2' type='checkbox' />
                                    Remember me
                                </p>
                                <p>Need Help?</p>
                            </div>

                        </form>
                        <button className="w-full flex items-center justify-center gap-x-3 py-2.5 border rounded-lg text-sm font-medium hover:bg-gray-50 duration-150 active:bg-gray-100">
                            <svg className="w-5 h-5" viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <g clip-path="url(#clip0_17_40)">
                                    <path d="M47.532 24.5528C47.532 22.9214 47.3997 21.2811 47.1175 19.6761H24.48V28.9181H37.4434C36.9055 31.8988 35.177 34.5356 32.6461 36.2111V42.2078H40.3801C44.9217 38.0278 47.532 31.8547 47.532 24.5528Z" fill="#4285F4" />
                                    <path d="M24.48 48.0016C30.9529 48.0016 36.4116 45.8764 40.3888 42.2078L32.6549 36.2111C30.5031 37.675 27.7252 38.5039 24.4888 38.5039C18.2275 38.5039 12.9187 34.2798 11.0139 28.6006H3.03296V34.7825C7.10718 42.8868 15.4056 48.0016 24.48 48.0016Z" fill="#34A853" />
                                    <path d="M11.0051 28.6006C9.99973 25.6199 9.99973 22.3922 11.0051 19.4115V13.2296H3.03298C-0.371021 20.0112 -0.371021 28.0009 3.03298 34.7825L11.0051 28.6006Z" fill="#FBBC04" />
                                    <path d="M24.48 9.49932C27.9016 9.44641 31.2086 10.7339 33.6866 13.0973L40.5387 6.24523C36.2 2.17101 30.4414 -0.068932 24.48 0.00161733C15.4055 0.00161733 7.10718 5.11644 3.03296 13.2296L11.005 19.4115C12.901 13.7235 18.2187 9.49932 24.48 9.49932Z" fill="#EA4335" />
                                </g>
                                <defs>
                                    <clipPath id="clip0_17_40">
                                        <rect width="48" height="48" fill="white" />
                                    </clipPath>
                                </defs>
                            </svg>
                            Continue with Google
                        </button>
                        <p className="text-center">Don't have an account? <a href="javascript:void(0)" className="font-medium text-indigo-600 hover:text-indigo-500">Sign up</a></p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
